package com.learning.corejava;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 2;
		switch(a)
		{
		case 1 :System.out.println("monday");
		break;
		case 2 :System.out.println("tuseday");
		break;
		default :System.out.println("error");
		break;
		}

	}

}
